/* Estilo general para el carrusel */
.carousel-container {
  max-width: 100%;
  overflow: hidden;
  position: relative;
  border-radius: 10px;
}

/* Estilo del carrusel */
.carousel {
  position: relative;
  width: 100%;
  max-width: 1620px;
  margin: auto;
}

/* Estilo de los items del carrusel */
.carousel-inner {
  position: relative;
  width: 100%;
  overflow: hidden;
  border-radius: 10px;
}

.carousel-item {
  height: 560px; /* Altura fija para las imágenes */
  background-color: #000; /* Color de fondo por si acaso la imagen no carga */
  border-radius: 10px;
}

/* Ajuste para el contenedor de la imagen */
.carousel-image {
  background-size: cover; /* Ajusta la imagen para cubrir el área del contenedor */
  background-position: center; /* Centra la imagen dentro del contenedor */
  width: 100%;
  height: 100%;
  border-radius: 10px;
}

/* Indicadores del carrusel */
/* Indicadores del carrusel */
.carousel-indicators {
  bottom: -20px; /* Mueve los indicadores más abajo */
  display: flex;
  justify-content: center;
  align-items: center;
}

.carousel-indicators li {
  width: 12px; /* Tamaño de los indicadores redondos */
  height: 12px;
  background-color: #ffffff; /* Color de los indicadores */
  border-radius: 50%; /* Hace los indicadores redondos */
  margin: 0 6px; /* Espaciado entre los indicadores */
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.carousel-indicators .active {
  background-color: #00ffff; /* Color del indicador activo */
}

/* Controles del carrusel */
.carousel-control-prev, .carousel-control-next {
  width: 5%;
  top: 50%;
  transform: translateY(-50%);
}

/* Asegura que los controles sean accesibles en diferentes tamaños */
.carousel-control-prev-icon, .carousel-control-next-icon {
  background-color: rgba(0, 0, 0, 0.5); /* Fondo semi-transparente para los iconos */
}

/* Adaptaciones para diferentes tamaños de pantalla */

/* Pantallas pequeñas (<=720px) */
@media (max-width: 720px) {
  .carousel-item {
    height: 300px; /* Ajusta la altura para pantallas pequeñas */
  }
  .carousel-item:nth-child(1) .carousel-image {
    background-image: url('/AorusMobile1.png'); /* Imagen para dispositivos móviles */
  }
  .carousel-item:nth-child(2) .carousel-image {
    background-image: url('/AorusMobile2.png'); /* Imagen para dispositivos móviles */
  }
}

/* Pantallas medianas (<=1080px) */
@media (max-width: 1080px) {
  .carousel-item {
    height: 400px; /* Ajusta la altura para pantallas medianas */
  }
}

/* Pantallas grandes (>=1440px) */
@media (min-width: 1440px) {
  .carousel-item {
    height: 560px; /* Mantén la altura original para pantallas grandes */
  }
}

.top-header-1 {
  color: #ffffff; /* Blanco */
  display: flex; /* Usa flexbox para centrar el contenido */
  font-size: 0.7rem; /* Tamaño de texto base para pantallas más grandes */
  text-align: center; /* Alinea el texto al centro */
}

.top-header-1 p {
  margin: 0;
  font-size: 0.8rem; /* Tamaño de texto base para párrafos */
}

.product-detail2 span {
  display: -webkit-box; /* Necessary for webkit-based browsers */
  -webkit-line-clamp: 3; /* Vendor-prefixed for WebKit browsers */
  line-clamp: 3; /* Standard property for modern browsers */
  -webkit-box-orient: vertical; /* For WebKit browsers */
  box-orient: vertical; /* Standard property */
  overflow: hidden; /* Hide text that overflows */
  text-overflow: ellipsis; /* Add ellipsis if the text is truncated */
  height: 4.5em; /* Ensure space for exactly 3 lines (adjust based on line-height) */
  line-height: 1.5em; /* Set line height for spacing between lines */
  width: 100%; /* Ensure span takes up full container width */
  white-space: normal; /* Allow wrapping of text */
}

.product-detail2 span::after {
  content: " ".repeat(100); /* Add extra spaces to force height if needed */
  visibility: hidden; /* Make these spaces invisible */
}

.product-detail2 h5 {
  margin-top: 2px; /* Añade un espacio de 2px arriba del <h5> */
}

.precios {
  display: flex;
  justify-content: center; /* Centra horizontalmente */
  align-items: center;     /* Centra verticalmente (opcional, si el contenedor tiene más altura) */
  font-size: ccalc(14px + (16 - 14) * ((100vw - 320px) / (1920 - 320)));
  font-weight: 700;
}


/* Estilos básicos */

.search-input {
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 50px 0 0 50px;
}

.search-button {
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 0 50px 50px 0;
  background-color: #dc3545;
  border-color: #dc3545;
}

/* Móviles (menor o igual a 576px) */
@media (max-width: 576px) {
  .input-group {
    max-width: 300px; /* Reduce el ancho */
  }
  .search-input, .search-button {
    font-size: 12px;
    padding: 10px 8px;
  }
}

/* Tablets (577px a 768px) */
@media (min-width: 577px) and (max-width: 768px) {
  .input-group {
    max-width: 300px;
  }
  .search-input, .search-button {
    font-size: 13px;
    padding: 10px 9px;
  }
}

/* Resolución estándar 1080p (769px a 1440px) */
@media (min-width: 769px) and (max-width: 1440px) {
  .input-group {
    max-width: 500px;
  }
  .search-input, .search-button {
    font-size: 14px;
    padding: 10px 10px;
  }
}

/* Resolución 2K (1441px a 2560px) */
@media (min-width: 1441px) and (max-width: 2560px) {
  .input-group {
    max-width: 700px;
  }
  .search-input, .search-button {
    font-size: 15px;
    padding: 7px 12px;
  }
}

/* Resoluciones mayores a 2K (2561px en adelante) */
@media (min-width: 2500px) {
  .input-group {
    max-width: 800px;
  }
  .search-input, .search-button {
    font-size: 16px;
    padding: 10px 14px;
  }
}

.search-input:focus {
  outline: none; /* Opcional: eliminar el contorno */
  border-color: #dc3545; /* Cambiar borde al recibir foco */
  box-shadow: 0 0 5px rgba(25, 135, 84, 0.5); /* Añadir efecto de sombra */
}

.img-fluid_marcas {
  max-width: 70%;
  height: auto; }

/*=====================
  23.breadcrumb CSS start
==========================*/

/* Default (aplica a 1440p y superiores hasta 4K) */
.breadcrumb-contain {
  font-size: calc(12px + (72 - 30) * ((100vw - 320px) / (1920 - 320)));
  margin-left: 430px;
  margin-top: 50px;
  margin-bottom: 10px;
}

/* iPhone y Android (hasta 480px de ancho) */
@media only screen and (max-width: 400px) {
  .breadcrumb-contain {
    margin-left: 15px;
    margin-top: 20px;
    margin-bottom: 10px;
  }
}

/* Tablets pequeños y móviles grandes (481px a 768px) */
@media only screen and (min-width: 410px) and (max-width: 758px) {
  .breadcrumb-contain {
    margin-left: 40px;
    margin-top: 25px;
    margin-bottom: 10px;
  }
}

/* Tablets y dispositivos medianos (769px a 1024px) */
@media only screen and (min-width: 760px) and (max-width: 1024px) {
  .breadcrumb-contain {
    font-size: calc(8px + (45 - 30) * ((100vw - 320px) / (1920 - 320)));
    margin-left: 100px;
    margin-top: 30px;
    margin-bottom: 10px;
  }
}

/* 720p (1025px a 1280px) */
@media only screen and (min-width: 1025px) and (max-width: 1280px) {
  .breadcrumb-contain {
    margin-left: 200px;
    margin-top: 40px;
    margin-bottom: 10px;
  }
}

/* 1080p (1281px a 1920px) */
@media only screen and (min-width: 1281px) and (max-width: 1920px) {
  .breadcrumb-contain {
    font-size: calc(5px + (42 - 30) * ((100vw - 320px) / (1920 - 320)));
    margin-left: 170px;
    margin-top: 45px;
    margin-bottom: 10px;
  }
}

/* 1440p (1921px a 2560px) */
@media only screen and (min-width: 1921px) and (max-width: 2560px) {
  .breadcrumb-contain {
    font-size: calc(8px + (34 - 30) * ((100vw - 320px) / (1920 - 320)));
    margin-left: 430px;
    margin-top: 50px;
    margin-bottom: 10px;
  }
}

/* 4K (2561px y superior) */
@media only screen and (min-width: 2550px) {
  .breadcrumb-contain {
    margin-left: 465px;
    margin-top: 60px;
    margin-bottom: 10px;
  }
}



