(function ($) {
    "use strict";

  if ($(".pedido-box").length == 0) return;


  let data = null;
  let datos;
  let transferencia = {};
  let dataGlobal = getObjectoJSON();
  if (typeof dataGlobal.TRANSFERENCIA != "undefined") {
    transferencia = dataGlobal.TRANSFERENCIA;
  }
  if (typeof dataGlobal.PEDIDO != "undefined") {
    data = dataGlobal.PEDIDO;
  }
  if (data == null) return;
console.log(data);

  datos = $(".success-text");
  if (data.idEstado < 0) datos.addClass("order-fail");
  datos.html("");
  datos.append($("<i aria-hidden='true'></i>").addClass(data.idEstado < 0?"fa fa-circle-xmark":"fa fa-check-circle"));
  if (data.idEstado < 0) 
    datos.append("<h2>Pedido no finalizado</h2>");
  else
    datos.append("<h2>Gracias por su Pedido</h2>");
  datos.append(
     $("<p>").append(
        "Pedido Nº" + data.nroOrden + (data.idEstado < 0?" / ANULADO":"") 
     )
  );
  if (data.idEstado < 0) {
    datos.append(
       $("<p>").append( 
         $("<button/>").append("Reintentar Pagar")
           .click(function (e) {
             e.preventDefault();
             location.href = data.urlRetry;      
           }) 
         )
    );
  }

  let $abortada = false;
  let $pagada = false;

  let api = false;
  let items = $(".pedido-box .product-order");
  items.html("");
  items.append("<h3>Detalle del Pedido</h3>");
  for(let p of data.productos) {
    items.append(addCartPedidoItem(p));
    if (p.api == 1) api = true;                                                                     
  }

  let sub = $("<ul/>");

  if (data.despacho != 0) {
    sub.append($("<li/>").append("Despacho").append("<span>"+fmtNumber(data.despacho)+"</span>"));
  }
  if (data.recargo != 0) {
    sub.append($("<li/>").append("Rechargo").append("<span>"+fmtNumber(data.recargo)+"</span>"));
  }

  items.append(
    $("<div/>").addClass("total-sec")
    .append( sub )
  );

  items.append(
    $("<div/>").addClass("final-total")
    .append(
      $("<h3>Total</h3>").append("<span>"+fmtNumber(data.total)+"</span>") 
    )
  );

  datos = $(".pedido-box .payment-mode");
  datos.html("");
  datos.append("<h4>Medio de Pago</h4>");
  datos.append(addCartPedido("Comercio:", data.comercio));
  datos.append(addCartPedido("Web:", data.comercioUrl));

  if (data.voucher != null) {
    datos.append(addCartPedido("Medio de Pago:", data.voucher.medio));
    if (data.voucher.codigoRespuesta == 0) {
      $pagada = true;
      if (data.voucher.codAutorizacion != null) {
        datos.append(addCartPedido("Codigo Autorizacion:", data.voucher.codAutorizacion));
      }
      datos.append(addCartPedido("Fecha Transaccion:", data.voucher.fechaTransaccion));
      if (data.voucher.glosaTipoPago != null) {
        datos.append(addCartPedido("Tipo de Pago:", data.voucher.glosaTipoPago));
      }
      if (data.voucher.numCuotas != null && data.voucher.numCuotas > 1) {
        datos.append(addCartPedido("Numero de Cuotas:", data.voucher.numCuotas));
      }
      if (data.voucher.ultDigitos != null) {
        datos.append(addCartPedido("Ultimos 4 digitos de la Tarjeta:", data.voucher.ultDigitos));
      }
    } else {
      if (data.voucher.codigoRespuesta == null) {
        datos.append(addCartPedido("Respuesta:", "Aun sin respuesta"));
        if (data.esTransferencia && data.urlTransfrencia != null) {
          datos.append(addCartPedido("Valida:", "<a target='_blank' href='" + data.urlTransfrencia + "'>Aqui</a>"));
        }
      } else {
        datos.append(addCartPedido("Error en pago:", data.voucher.glosaRespuesta));
        $abortada = true;
      }
    }
  }

  datos = $(".pedido-box .order-summary");
  datos.html("");
  if (!$abortada) {
    datos.append(addCartPedidoSummary("Rut:", data.cliente.rutFull));
    datos.append(addCartPedidoSummary("Nombre:", data.cliente.nombre));
    datos.append(addCartPedidoSummary("Direccion:", data.cliente.facturacion.direccionCompleta));  
    datos.append(addCartPedidoSummary("Tipo Documento:", data.idTipo));
  }

  datos = $(".pedido-box .delivery-sec");
  datos.html("");
  datos.append("<h3>"+ data.formaentrega+"</h3>");

  datos = $(".pedido-box .order-direccion");
  datos.html("");
  if (api) {
    datos.append(addCartPedidoSummary(getApiNota(), ""));
  }
  if (data.esDespacho) {
    datos.append(addCartPedidoSummary("Transporte:", data.transporte));
    datos.append(addCartPedidoSummary("Despacho:", data.cliente.despacho.direccionCompleta));
  }


  if (data.idEstado >= 0) {
    gtag('event', 'conversion_event_purchase', {
      // <event_parameters>
    });
  }


