function addCartPedidoItem(item) {
  let e = $('<div/>').addClass("row product-order-detail");

  e.append(
    $("<div/>")
      .addClass("col-6 order_detail")
      .append(
        $("<div/>").append( $("<h4/>").append("Nombre"))
        .append( $("<h5/>").append(getApi(item) + item.codigo + " " + item.nombre) )
      )
  );

  e.append(
    $("<div/>")
      .addClass("col-3 order_detail")
      .append(
        $("<div/>").append( $("<h4/>").append("Cantidad"))
        .append( $("<h5/>").append(item.cantidad) )
      )
  );

  e.append(
    $("<div/>")
      .addClass("col-3 order_detail")
      .append(
        $("<div/>").append( $("<h4/>").append("Cantidad"))
        .append( $("<h5/>").append(fmtNumber(item.total)) )
      )
  );

  return e;
}

function addCartPedido(label, total) {
  return $("<p/>").append("<b>"+label + "</b> " + total);
}

function addCartPedidoSummary(label, total) {
  return $("<li/>").append("<b>"+label + "</b> " + total);
}

