function sphinxShopFiltro() {
  let ret = {};
  ret.titulo = ".shop_title h1";
  ret.divProductos = ".shop_toolbar_wrapper";
  ret.mensaje = ".page_mensaje div";
  ret.paginacion = ".pagination";

  ret.totales = [".search-count h5", ".product-search-count-bottom h5"];
  ret.productos = ".product-wrapper-grid .row";
  ret.filtros = "#filtroShop";
  ret.filtrosItem = "#filtroShop .collection-collapse-block";
  ret.getCaluga = function (p, viewMode) {
    return $("<div/>").addClass("col-xl-3").addClass("col-md-4").addClass("col-6").addClass("col-grid-box")
       .append(WebSphinxFiltro.dibujaCaluga(p));
  };
  ret.postCaluga = function () {
    feather.replace();
    $('.collapse-block-title').on('click', function (e) {
      e.preventDefault;
      var speed = 300;
      var thisItem = $(this).parent(),
        nextLevel = $(this).next('.collection-collapse-block-content');
      if (thisItem.hasClass('open')) {
        thisItem.removeClass('open');
        nextLevel.slideUp(speed);
      }
      else {
        thisItem.addClass('open');
        nextLevel.slideDown(speed);
      }
    });
  };
  return ret;
}

