(function ($) {
  "use strict";

  $(".box-address").hide();

  $(".box-menu ul li").each(function () {
    let obj = $(this);
    if (typeof obj.attr("tab") == "undefined") return;
    if (obj.hasClass("active")) {
      $("."+obj.attr("tab")).show();
    } else {
      $("."+obj.attr("tab")).hide();
    }
    obj.click(function () {
      $(".box-menu ul li").each(function () {
        let obj = $(this);
        $(this).removeClass("active");
        $("."+obj.attr("tab")).hide();
      });
      let obj = $(this);
      obj.addClass("active");
      $("."+obj.attr("tab")).show();
    });
  });

})(jQuery);
